const dev = {
  API_URL: 'http://localhost:3001'
};

const prod = {
  API_URL: process.env.REACT_APP_API_URL || 'https://your-backend-url.herokuapp.com'
};

const config = process.env.NODE_ENV === 'production' ? prod : dev;

export default config;
